fn main() -> Result<(), Box<dyn Error>> {
    // Read CSV and extract data
    let mut reader = Reader::from_path("data.csv")?;
    let records = reader.records();
    let (smiles, labels): (Vec<_>, Vec<_>) = records
        .map(|r| r.unwrap())
        .map(|r| (r[0].to_owned(), r[1].parse().unwrap()))
        .unzip();

    // Clean SMILES
    let cleaned_smiles = smiles
        .iter()
        .map(|s| SmilesWriter::new().write(Smiles::new(s).unwrap().iter()))
        .collect::<Vec<_>>();

    // Remove duplicates
    let cleaned_smiles = cleaned_smiles.into_iter().unique().collect::<Vec<_>>();

    // Create and train model
    let (train_smiles, test_smiles, train_labels, test_labels) =
        create_train_test_split(&cleaned_smiles, &labels, 0.8);
    let model = create_and_train_model(train_smiles, train_labels)?;

    // Evaluate
    evaluate_model(&model, &test_smiles, &test_labels)?;

    Ok(())
}

fn create_train_test_split(
    smiles: &[String],
    labels: &[u32],
    test_size: f64,
) -> (Vec<String>, Vec<String>, Vec<u32>, Vec<u32>) {
    let mut rng = rand::thread_rng();
    let len = smiles.len();
    let test_len = (len as f64 * test_size) as usize;
    let mut indices = (0..len).collect::<Vec<_>>();
    indices.shuffle(&mut rng);

    let train_indices = &indices[test_len..];
    let test_indices = &indices[..test_len];

    let train_smiles = train_indices.iter().map(|&i| smiles[i].clone()).collect();
    let test_smiles = test_indices.iter().map(|&i| smiles[i].clone()).collect();
    let train_labels = train_indices.iter().map(|&i| labels[i]).collect();
    let test_labels = test_indices.iter().map(|&i| labels[i]).collect();

    (train_smiles, test_smiles, train_labels, test_labels)
}

fn create_and_train_model(
    smiles: Vec<String>,
    labels: Vec<u32>,
) -> Result<tensorflow::Sequential, Box<dyn Error>> {
    let mut model = tensorflow::Sequential::new();
    model.add(tensorflow::layers::LSTM::new(32));
    model.add(tensorflow::layers::Dense::new(1, tensorflow::activations::sigmoid()));

    let (x_train, y_train) = create_dataset(smiles, labels)?;

    let mut opt = tensorflow::optimizers::Adam::default();
    let loss = tensorflow::losses::BinaryCrossentropy::new();
    model.compile(&opt, loss, &[tensorflow::metrics::Accuracy::new()])?;

    model.fit(&x_train, &y_train, 64, Some(10))?;

    Ok(model)
}

fn create_dataset(
    smiles: Vec<String>,
    labels: Vec<u32>,
) -> Result<(tensorflow::Tensor, tensorflow::Tensor), Box<dyn Error>> {
    let mut dataset = tensorflow::data::Dataset::new().batch(smiles.len() as u64);
    for (smile, label) in smiles.iter().zip(labels.iter()) {
        let example = Example::builder()
            .add_feature(
                tensorflow::Feature::BytesList(
                    vec![tensorflow::BytesList::from(smile.as_bytes())].into(),
                ),
            )
            .add_feature(tensorflow::Feature::Int64List(vec![*label as i64].into()))
            .build()?;
        dataset = dataset.add_example(example);
    }

    let (x_train, y_train) = dataset.unpack();

    Ok((x_train, y_train))
}

