```csharp
using System;
using System.Net;
using System.Net.Http;
using System.Web.Http;

namespace CalculatorAPI
{
    public class CalculatorController : ApiController
    {
        [HttpGet]
        [Route("api/calculator/add")]
        public IHttpActionResult Add(int num1, int num2)
        {
            int result = num1 + num2;
            return Ok(result);
        }

        [HttpGet]
        [Route("api/calculator/subtract")]
        public IHttpActionResult Subtract(int num1, int num2)
        {
            int result = num1 - num2;
            return Ok(result);
        }

        [HttpGet]
        [Route("api/calculator/divide")]
        public IHttpActionResult Divide(int num1, int num2)
        {
            if (num2 == 0)
            {
                return BadRequest("Cannot divide by zero.");
            }

            int result = num1 / num2;
            return Ok(result);
        }
    }
}
```

