        JsonReader reader = new JsonTextReader(new StringReader(jsonString));

        // Create a dynamic object to hold the deserialized JSON data.
        result = new JObject();

        // Deserialize the JSON into the dynamic object.
        reader.Read();
        result = reader.ReadObject();
    }
    catch (Exception e)
